         PLPGSQL
         boolean



       Add a column of a given type to a table if it is missing

DECLARE
  p_namespace alias for $1;
  p_table     alias for $2;
  p_field     alias for $3;
  p_type      alias for $4;
  v_row       record;
  v_query     text;
BEGIN
  select 1 into v_row from pg_namespace n, pg_class c, pg_attribute a
     where slon_quote_brute(n.nspname) = p_namespace and 
         c.relnamespace = n.oid and
         slon_quote_brute(c.relname) = p_table and
         a.attrelid = c.oid and
         slon_quote_brute(a.attname) = p_field;
  if not found then
    raise notice 'Upgrade table %.% - add field %', p_namespace, p_table, p_field;
    v_query := 'alter table ' || p_namespace || '.' || p_table || ' add column ';
    v_query := v_query || p_field || ' ' || p_type || ';';
    execute v_query;
    return 't';
  else
    return 'f';
  end if;
END;


        Function Properties

        Language
        Return Type

         PLPGSQL
         integer



       Add partial indexes, if possible, to the unused sl_log_? table for
all origin nodes, and drop any that are no longer needed.

This function presently gets run any time set origins are manipulated
(FAILOVER, STORE SET, MOVE SET, DROP SET), as well as each time the
system switches between sl_log_1 and sl_log_2.

DECLARE
	v_current_status	int4;
	v_log			int4;
	v_dummy		record;
	v_dummy2	record;
	idef 		text;
	v_count		int4;
        v_iname         text;
BEGIN
	v_count := 0;
	select last_value into v_current_status from sl_log_status;

	-- If status is 2 or 3 --> in process of cleanup --> unsafe to create indices
	if v_current_status in (2, 3) then
		return 0;
	end if;

	if v_current_status = 0 then   -- Which log should get indices?
		v_log := 2;
	else
		v_log := 1;
	end if;
--                                       PartInd_test_db_sl_log_2-node-1
	-- Add missing indices...
	for v_dummy in select distinct set_origin from sl_set loop
            v_iname := 'PartInd_schemadoc_sl_log_' || v_log || '-node-' || v_dummy.set_origin;
	    -- raise notice 'Consider adding partial index % on sl_log_%', v_iname, v_log;
	    -- raise notice 'schema: [_schemadoc] tablename:[sl_log_%]', v_log;
            select * into v_dummy2 from pg_catalog.pg_indexes where tablename = 'sl_log_' || v_log and  indexname = v_iname;
            if not found then
		-- raise notice 'index was not found - add it!';
		idef := 'create index "PartInd_schemadoc_sl_log_' || v_log || '-node-' || v_dummy.set_origin ||
                        '" on sl_log_' || v_log || ' USING btree(log_xid xxid_ops) where (log_origin = ' || v_dummy.set_origin || ');';
		execute idef;
		v_count := v_count + 1;
            else
                -- raise notice 'Index % already present - skipping', v_iname;
            end if;
	end loop;

	-- Remove unneeded indices...
	for v_dummy in select indexname from pg_catalog.pg_indexes i where i.tablename = 'sl_log_' || v_log and
                       i.indexname like ('PartInd_schemadoc_sl_log_' || v_log || '-node-%') and
                       not exists (select 1 from sl_set where
				i.indexname = 'PartInd_schemadoc_sl_log_' || v_log || '-node-' || set_origin)
	loop
		-- raise notice 'Dropping obsolete index %d', v_dummy.indexname;
		idef := 'drop index "' || v_dummy.indexname || '";';
		execute idef;
		v_count := v_count - 1;
	end loop;
	return v_count;
END

Function Properties
Language Return Type
PLPGSQL integer
alterTableForReplication(tab_id)
Sets up a table for replication. On the origin, this involves adding the "logTrigger()" trigger to the table.
On a subscriber node, this involves disabling triggers and rules, and adding in the trigger that denies write access to replicated tables.
declare p_tab_id alias for $1; v_no_id int4; v_tab_row record; v_tab_fqname text; v_tab_attkind text; v_n int4; v_trec record; v_tgbad boolean; begin -- ---- -- Grab the central configuration lock -- ---- lock table sl_config_lock;
-- ---- -- Get our local node ID -- ---- v_no_id := getLocalNodeId('_schemadoc');
-- ---- -- Get the sl_table row and the current origin of the table. -- Verify that the table currently is NOT in altered state. -- ---- select T.tab_reloid, T.tab_set, T.tab_idxname, T.tab_altered, S.set_origin, PGX.indexrelid, slon_quote_brute(PGN.nspname) || '.' || slon_quote_brute(PGC.relname) as tab_fqname into v_tab_row from sl_table T, sl_set S, "pg_catalog".pg_class PGC, "pg_catalog".pg_namespace PGN, "pg_catalog".pg_index PGX, "pg_catalog".pg_class PGXC where T.tab_id = p_tab_id and T.tab_set = S.set_id and T.tab_reloid = PGC.oid and PGC.relnamespace = PGN.oid and PGX.indrelid = T.tab_reloid and PGX.indexrelid = PGXC.oid and PGXC.relname = T.tab_idxname for update; if not found then raise exception 'Slony-I: alterTableForReplication(): Table with id % not found', p_tab_id; end if; v_tab_fqname = v_tab_row.tab_fqname; if v_tab_row.tab_altered then raise exception 'Slony-I: alterTableForReplication(): Table % is already in altered state', v_tab_fqname; end if;
v_tab_attkind := determineAttKindUnique(v_tab_row.tab_fqname, v_tab_row.tab_idxname);
execute 'lock table ' || v_tab_fqname || ' in access exclusive mode';
-- ---- -- Procedures are different on origin and subscriber -- ---- if v_no_id = v_tab_row.set_origin then -- ---- -- On the Origin we add the log trigger to the table and done -- ---- execute 'create trigger "_schemadoc_logtrigger_' || p_tab_id || '" after insert or update or delete on ' || v_tab_fqname || ' for each row execute procedure logTrigger (''_schemadoc'', ''' || p_tab_id || ''', ''' || v_tab_attkind || ''');'; else -- ---- -- On the subscriber the thing is a bit more difficult. We want -- to disable all user- and foreign key triggers and rules. -- ----
-- ---- -- Check to see if there are any trigger conflicts... -- ---- v_tgbad := 'false'; for v_trec in select pc.relname, tg1.tgname from "pg_catalog".pg_trigger tg1, "pg_catalog".pg_trigger tg2, "pg_catalog".pg_class pc, "pg_catalog".pg_index pi, sl_table tab where tg1.tgname = tg2.tgname and -- Trigger names match tg1.tgrelid = tab.tab_reloid and -- trigger 1 is on the table pi.indexrelid = tg2.tgrelid and -- trigger 2 is on the index pi.indrelid = tab.tab_reloid and -- indexes table is this table pc.oid = tab.tab_reloid loop raise notice 'Slony-I: alterTableForReplication(): multiple instances of trigger % on table %', v_trec.tgname, v_trec.relname; v_tgbad := 'true'; end loop; if v_tgbad then raise exception 'Slony-I: Unable to disable triggers'; end if;
-- ---- -- Disable all existing triggers -- ---- update "pg_catalog".pg_trigger set tgrelid = v_tab_row.indexrelid where tgrelid = v_tab_row.tab_reloid and not exists ( select true from sl_table TAB, sl_trigger TRIG where TAB.tab_reloid = tgrelid and TAB.tab_id = TRIG.trig_tabid and TRIG.trig_tgname = tgname ); get diagnostics v_n = row_count; if v_n > 0 then update "pg_catalog".pg_class set reltriggers = reltriggers - v_n where oid = v_tab_row.tab_reloid; end if;
-- ---- -- Disable all existing rules -- ---- update "pg_catalog".pg_rewrite set ev_class = v_tab_row.indexrelid where ev_class = v_tab_row.tab_reloid; get diagnostics v_n = row_count; if v_n > 0 then update "pg_catalog".pg_class set relhasrules = false where oid = v_tab_row.tab_reloid; end if;
-- ---- -- Add the trigger that denies write access to replicated tables -- ---- execute 'create trigger "_schemadoc_denyaccess_' || p_tab_id || '" before insert or update or delete on ' || v_tab_fqname || ' for each row execute procedure denyAccess (''_schemadoc'');'; end if;
-- ---- -- Mark the table altered in our configuration -- ---- update sl_table set tab_altered = true where tab_id = p_tab_id;
return p_tab_id; end;

Function Properties
Language Return Type
PLPGSQL integer
alterTableRestore (tab_id)
Restores table tab_id from being replicated.
On the origin, this simply involves dropping the "logtrigger" trigger.
On subscriber nodes, this involves dropping the "denyaccess" trigger, and restoring user triggers and rules.
declare p_tab_id alias for $1; v_no_id int4; v_tab_row record; v_tab_fqname text; v_n int4; begin -- ---- -- Grab the central configuration lock -- ---- lock table sl_config_lock;
-- ---- -- Get our local node ID -- ---- v_no_id := getLocalNodeId('_schemadoc');
-- ---- -- Get the sl_table row and the current tables origin. Check -- that the table currently IS in altered state. -- ---- select T.tab_reloid, T.tab_set, T.tab_altered, S.set_origin, PGX.indexrelid, slon_quote_brute(PGN.nspname) || '.' || slon_quote_brute(PGC.relname) as tab_fqname into v_tab_row from sl_table T, sl_set S, "pg_catalog".pg_class PGC, "pg_catalog".pg_namespace PGN, "pg_catalog".pg_index PGX, "pg_catalog".pg_class PGXC where T.tab_id = p_tab_id and T.tab_set = S.set_id and T.tab_reloid = PGC.oid and PGC.relnamespace = PGN.oid and PGX.indrelid = T.tab_reloid and PGX.indexrelid = PGXC.oid and PGXC.relname = T.tab_idxname for update; if not found then raise exception 'Slony-I: alterTableRestore(): Table with id % not found', p_tab_id; end if; v_tab_fqname = v_tab_row.tab_fqname; if not v_tab_row.tab_altered then raise exception 'Slony-I: alterTableRestore(): Table % is not in altered state', v_tab_fqname; end if;
execute 'lock table ' || v_tab_fqname || ' in access exclusive mode';
-- ---- -- Procedures are different on origin and subscriber -- ---- if v_no_id = v_tab_row.set_origin then -- ---- -- On the Origin we just drop the trigger we originally added -- ---- execute 'drop trigger "_schemadoc_logtrigger_' || p_tab_id || '" on ' || v_tab_fqname; else -- ---- -- On the subscriber drop the denyAccess trigger -- ---- execute 'drop trigger "_schemadoc_denyaccess_' || p_tab_id || '" on ' || v_tab_fqname;
-- ---- -- Restore all original triggers -- ---- update "pg_catalog".pg_trigger set tgrelid = v_tab_row.tab_reloid where tgrelid = v_tab_row.indexrelid; get diagnostics v_n = row_count; if v_n > 0 then update "pg_catalog".pg_class set reltriggers = reltriggers + v_n where oid = v_tab_row.tab_reloid; end if;
-- ---- -- Restore all original rewrite rules -- ---- update "pg_catalog".pg_rewrite set ev_class = v_tab_row.tab_reloid where ev_class = v_tab_row.indexrelid; get diagnostics v_n = row_count; if v_n > 0 then update "pg_catalog".pg_class set relhasrules = true where oid = v_tab_row.tab_reloid; end if;
end if;
-- ---- -- Mark the table not altered in our configuration -- ---- update sl_table set tab_altered = false where tab_id = p_tab_id;
return p_tab_id; end;


