       moveSet(set_id, new_origin)

Generate MOVE_SET event to request that the origin for set set_id be moved to node new_origin

declare
	p_set_id			alias for $1;
	p_new_origin		alias for $2;
	v_local_node_id		int4;
	v_set_row			record;
	v_sub_row			record;
	v_sync_seqno		int8;
	v_lv_row			record;
begin
	-- ----
	-- Grab the central configuration lock
	-- ----
	lock table sl_config_lock;

	-- ----
	-- Check that the set is locked and that this locking
	-- happened long enough ago.
	-- ----
	v_local_node_id := getLocalNodeId('_schemadoc');
	select * into v_set_row from sl_set
			where set_id = p_set_id
			for update;
	if not found then
		raise exception 'Slony-I: set % not found', p_set_id;
	end if;
	if v_set_row.set_origin <> v_local_node_id then
		raise exception 'Slony-I: set % does not originate on local node',
				p_set_id;
	end if;
	if v_set_row.set_locked isnull then
		raise exception 'Slony-I: set % is not locked', p_set_id;
	end if;
	if v_set_row.set_locked > getMinXid() then
		raise exception 'Slony-I: cannot move set % yet, transactions < % are still in progress',
				p_set_id, v_set_row.set_locked;
	end if;

	-- ----
	-- Unlock the set
	-- ----
	perform unlockSet(p_set_id);

	-- ----
	-- Check that the new_origin is an active subscriber of the set
	-- ----
	select * into v_sub_row from sl_subscribe
			where sub_set = p_set_id
			and sub_receiver = p_new_origin;
	if not found then
		raise exception 'Slony-I: set % is not subscribed by node %',
				p_set_id, p_new_origin;
	end if;
	if not v_sub_row.sub_active then
		raise exception 'Slony-I: subsctiption of node % for set % is inactive',
				p_new_origin, p_set_id;
	end if;

	-- ----
	-- Reconfigure everything
	-- ----
	perform moveSet_int(p_set_id, v_local_node_id,
			p_new_origin, 0);

	perform RebuildListenEntries();

	-- ----
	-- At this time we hold access exclusive locks for every table
	-- in the set. But we did move the set to the new origin, so the
	-- createEvent() we are doing now will not record the sequences.
	-- ----
	v_sync_seqno := createEvent('_schemadoc', 'SYNC');
	insert into sl_seqlog 
			(seql_seqid, seql_origin, seql_ev_seqno, seql_last_value)
			select seq_id, v_local_node_id, v_sync_seqno, seq_last_value
			from sl_seqlastvalue
			where seq_set = p_set_id;

	-- ----
	-- Finally we generate the real event
	-- ----
	return createEvent('_schemadoc', 'MOVE_SET', 
			p_set_id::text, v_local_node_id::text, p_new_origin::text);
end;

Function Properties
Language Return Type
PLPGSQL integer
moveSet(set_id, old_origin, new_origin, wait_seqno)
Process MOVE_SET event to request that the origin for set set_id be moved from old_origin to node new_origin
declare p_set_id alias for $1; p_old_origin alias for $2; p_new_origin alias for $3; p_wait_seqno alias for $4; v_local_node_id int4; v_tab_row record; v_sub_row record; v_sub_node int4; v_sub_last int4; v_sub_next int4; v_last_sync int8; begin -- ---- -- Grab the central configuration lock -- ---- lock table sl_config_lock;
-- ---- -- Get our local node ID -- ---- v_local_node_id := getLocalNodeId('_schemadoc');
-- ---- -- If we are the old or new origin of the set, we need to -- remove the log trigger from all tables first. -- ---- if v_local_node_id = p_old_origin or v_local_node_id = p_new_origin then for v_tab_row in select tab_id from sl_table where tab_set = p_set_id order by tab_id loop perform alterTableRestore(v_tab_row.tab_id); end loop; end if;
-- On the new origin, raise an event - ACCEPT_SET if v_local_node_id = p_new_origin then -- Create a SYNC event as well so that the ACCEPT_SET has -- the same snapshot as the last SYNC generated by the new -- origin. This snapshot will be used by other nodes to -- finalize the setsync status. perform createEvent('_schemadoc', 'SYNC', NULL); perform createEvent('_schemadoc', 'ACCEPT_SET', p_set_id::text, p_old_origin::text, p_new_origin::text, p_wait_seqno::text); end if;
-- ---- -- Next we have to reverse the subscription path -- ---- v_sub_last = p_new_origin; select sub_provider into v_sub_node from sl_subscribe where sub_set = p_set_id and sub_receiver = p_new_origin; if not found then raise exception 'Slony-I: subscription path broken in moveSet_int'; end if; while v_sub_node <> p_old_origin loop -- ---- -- Tracing node by node, the old receiver is now in -- v_sub_last and the old provider is in v_sub_node. -- ----
-- ---- -- Get the current provider of this node as next -- and change the provider to the previous one in -- the reverse chain. -- ---- select sub_provider into v_sub_next from sl_subscribe where sub_set = p_set_id and sub_receiver = v_sub_node for update; if not found then raise exception 'Slony-I: subscription path broken in moveSet_int'; end if; update sl_subscribe set sub_provider = v_sub_last where sub_set = p_set_id and sub_receiver = v_sub_node;
v_sub_last = v_sub_node; v_sub_node = v_sub_next; end loop;
-- ---- -- This includes creating a subscription for the old origin -- ---- insert into sl_subscribe (sub_set, sub_provider, sub_receiver, sub_forward, sub_active) values (p_set_id, v_sub_last, p_old_origin, true, true); if v_local_node_id = p_old_origin then select coalesce(max(ev_seqno), 0) into v_last_sync from sl_event where ev_origin = p_new_origin and ev_type = 'SYNC'; if v_last_sync > 0 then insert into sl_setsync (ssy_setid, ssy_origin, ssy_seqno, ssy_minxid, ssy_maxxid, ssy_xip, ssy_action_list) select p_set_id, p_new_origin, v_last_sync, ev_minxid, ev_maxxid, ev_xip, NULL from sl_event where ev_origin = p_new_origin and ev_seqno = v_last_sync; else insert into sl_setsync (ssy_setid, ssy_origin, ssy_seqno, ssy_minxid, ssy_maxxid, ssy_xip, ssy_action_list) values (p_set_id, p_new_origin, '0', '0', '0', '', NULL); end if; end if;
-- ---- -- Now change the ownership of the set. -- ---- update sl_set set set_origin = p_new_origin where set_id = p_set_id;
-- ---- -- On the new origin, delete the obsolete setsync information -- and the subscription. -- ---- if v_local_node_id = p_new_origin then delete from sl_setsync where ssy_setid = p_set_id; else if v_local_node_id <> p_old_origin then -- -- On every other node, change the setsync so that it will -- pick up from the new origins last known sync. -- delete from sl_setsync where ssy_setid = p_set_id; select coalesce(max(ev_seqno), 0) into v_last_sync from sl_event where ev_origin = p_new_origin and ev_type = 'SYNC'; if v_last_sync > 0 then insert into sl_setsync (ssy_setid, ssy_origin, ssy_seqno, ssy_minxid, ssy_maxxid, ssy_xip, ssy_action_list) select p_set_id, p_new_origin, v_last_sync, ev_minxid, ev_maxxid, ev_xip, NULL from sl_event where ev_origin = p_new_origin and ev_seqno = v_last_sync; else insert into sl_setsync (ssy_setid, ssy_origin, ssy_seqno, ssy_minxid, ssy_maxxid, ssy_xip, ssy_action_list) values (p_set_id, p_new_origin, '0', '0', '0', '', NULL); end if; end if; end if; delete from sl_subscribe where sub_set = p_set_id and sub_receiver = p_new_origin;
-- Regenerate sl_listen since we revised the subscriptions perform RebuildListenEntries();
-- Run addPartialLogIndices() to try to add indices to unused sl_log_? table perform addPartialLogIndices();
-- ---- -- If we are the new or old origin, we have to -- put all the tables into altered state again. -- ---- if v_local_node_id = p_old_origin or v_local_node_id = p_new_origin then for v_tab_row in select tab_id from sl_table where tab_set = p_set_id order by tab_id loop perform alterTableForReplication(v_tab_row.tab_id); end loop; end if;
return p_set_id; end;


        Function Properties

        Language
        Return Type

         PLPGSQL
         SET OF integer



       ReachableFromNode(receiver, blacklist)

Find all nodes that <receiver> can receive events from without
using nodes in <blacklist> as a relay.

declare
	v_node alias for $1 ;
	v_blacklist alias for $2 ;
	v_ignore int4[] ;
	v_reachable_edge_last int4[] ;
	v_reachable_edge_new int4[] default '{}' ;
	v_server record ;
begin
	v_reachable_edge_last := array[v_node] ;
	v_ignore := v_blacklist || array[v_node] ;
	return next v_node ;
	while v_reachable_edge_last != '{}' loop
		v_reachable_edge_new := '{}' ;
		for v_server in select pa_server as no_id
			from sl_path
			where pa_client = ANY(v_reachable_edge_last) and pa_server != ALL(v_ignore)
		loop
			if v_server.no_id != ALL(v_ignore) then
				v_ignore := v_ignore || array[v_server.no_id] ;
				v_reachable_edge_new := v_reachable_edge_new || array[v_server.no_id] ;
				return next v_server.no_id ;
			end if ;
		end loop ;
		v_reachable_edge_last := v_reachable_edge_new ;
	end loop ;
	return ;
end ;


        Function Properties

        Language
        Return Type

         PLPGSQL
         integer



       RebuildListenEntries()

Invoked by various subscription and path modifying functions, this
rewrites the sl_listen entries, adding in all the ones required to
allow communications between nodes in the Slony-I cluster.

declare
	v_receiver record ;
	v_provider record ;
	v_origin record ;
	v_reachable int4[] ;
begin
	-- First remove the entire configuration
	delete from sl_listen;

	-- Loop over every possible pair of receiver and provider
	for v_receiver in select no_id from sl_node loop
		for v_provider in select pa_server as no_id from sl_path where pa_client = v_receiver.no_id loop

			-- Find all nodes that v_provider.no_id can receiver events from without using v_receiver.no_id			
			for v_origin in select * from ReachableFromNode(v_provider.no_id, array[v_receiver.no_id]) as r(no_id) loop

				-- If v_receiver.no_id subscribes a set from v_provider.no_id, events have to travel the same
				-- path as the data. Ignore possible sl_listen that would break that rule.
				perform 1 from sl_subscribe
					join sl_set on sl_set.set_id = sl_subscribe.sub_set
		 			where
						sub_receiver = v_receiver.no_id and
						sub_provider != v_provider.no_id and
						set_origin = v_origin.no_id ;
				if not found then
					insert into sl_listen (li_receiver, li_provider, li_origin)
						values (v_receiver.no_id, v_provider.no_id, v_origin.no_id) ;
				end if ;


			end loop ;

		end loop ;
	end loop ;

	return null ;
end ;

