	insert into sl_set
			(set_id, set_origin, set_comment) values
			(p_set_id, v_local_node_id, p_set_comment);

	return createEvent('_schemadoc', 'STORE_SET', 
			p_set_id::text, v_local_node_id::text, p_set_comment::text);
end;


